$("#sections").on("change",function(){const n=$("#sections option:selected").val();$(".content").empty(),$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${n}.json?api-key=3ihvPWNhGBkw0DWmfc2rpg2te3TVeDov`}).done(function(n){for(let t=0;t<12;t++){let s=n.results[t].url,e=n.results[t].multimedia[4].url,l=n.results[t].abstract;$(".content").append(`<section class="content">\n         <ul> \n            <li class="article">\n            <div class="news-img">  \n            <a href id="article-link" alt="url" target="_blank"="${s}">\n                <img src =${e}>\n                <p class="summary">${l}</p>\n            </a>\n            </div>\n            </li>\n        </ul>\n        </section>`)}})});