$("#sections").on("change",function(){const t=$("#sections option:selected").val();$(".content").empty(),$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=3ihvPWNhGBkw0DWmfc2rpg2te3TVeDov`}).done(function(t){for(let e=0;e<12;e++){let n=t.results[e].url,s=t.results[e].multimedia[4].url,a=t.results[e].abstract;$(".content").append(`<ul> \n            <li class="article">\n            <a href="${n}" id="article-link" alt="url" target="_blank">\n                <img src =${s}>\n            </a>\n            <p class="summary">${a}</p>\n            </li>\n        </ul>`),$(".main-header").css({height:"300px","margin-bottom":"1rem"}),$(".main-header img").css({height:"150px","padding-top":"3rem"})}})});