function loader(){$(".spinner").show(),console.log("visible")}function loaderDone(){setTimeout(function(){$(".spinner").hide()},3e3)}$(".spinner").hide(),$("#sections").on("change",function(){loader();const e=$("#sections option:selected").val();$(".content").empty(),$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${e}.json?api-key=3ihvPWNhGBkw0DWmfc2rpg2te3TVeDov`}).done(function(e){loaderDone(),$(".main-header, .logo, .section").addClass("loaded");let n=0;for(let t=0;t<12;t++)if(e.results[t].multimedia.length>=5&&n<12){n+=1;let s=e.results[t].url,l=e.results[t].multimedia[4].url,i=e.results[t].abstract;$(".content").append(`<ul class="articles"> \n        <li class="article">\n        <a href="${s}" id="article-link" alt="url" target="_blank">\n        <img src =${l}>\n        </a>\n        <p class="summary">${i}</p>\n        </li>\n        </ul>`)}})});